	/**
     * Retrieves EAS (Microsoft/Windows) time zone information
     * 
     * @since Release 1.0.0
     * 
     * @param string $name  eas time zone name
     * 
     * @return object time zone information object on success, or null on failure
     */ 
    public static function findZone(?string $name) : ?object {

		if (isset(self::$easzones[$name])) {
			return json_decode(self::$easzones[$name]);
		} else {
			return null;
		}

    }

	/**
     * Retrieves EAS (Microsoft/Windows) time zone mutation information
     * 
     * @since Release 1.0.0
     * 
     * @param string|null $name				eas time zone name
	 * @param DateTime|Int|null $date		date to find mutation for
     * 
     * @return object time zone information object on success, or null on failure
     */ 
    public static function findZoneMutation(string|null $name, \DateTime|Int|null $date = null, bool $flag = false) : object|null {

		$zone = self::findZone($name);

		// evaluate if time zone was found
		if (!isset($zone)) {
			// evaluate if default flag was set
			if ($flag) {
				$zone = self::findZone('UTC');
			}
			else {
				return null;
			}
		}

		// evaluate if date is in DateTime format
		if ($date instanceof \DateTime) {
			// convert date to epoch time stamp
			$date = $date->getTimestamp();
		}
		// evaluate if date is null
		elseif ($date === null) {
			// assign current epoch time stamp if one was not provided
			$date = time();
		}

		foreach ($zone->Mutations as $entry) {
			if ($entry->Effective > $date) {
				break;
			} else {
				$mutation = $entry;
			}
		}

		return $mutation;

    }

	/**
     * Converts IANA time zone name to EAS (Microsoft/Windows) time zone name
     * 
     * @since Release 1.0.0
     * 
     * @param string $name  iana time zone name
     * 
     * @return string valid EWS time zone name on success, or null on failure
     */ 
	public static function fromIANA(?string $name): ?string {

		if (isset(self::$ianatoeas[$name])) {
			return self::$ianatoeas[$name];
		} else {
			return null;
		}

    }

	/**
     * Converts EAS (Microsoft/Windows) time zone name to IANA time zone name
     * 
     * @since Release 1.0.0
     * 
     * @param string $name  eas time zone name
     * 
     * @return string valid IANA time zone name on success, or null on failure
     */ 
	public static function toIANA(?string $name): ?string {

		if (isset(self::$eastoiana[$name])) {
			return self::$eastoiana[$name];
		} else {
			return null;
		}

    }

	/**
     * Converts DateTimeZone object to EAS (Microsoft/Windows) time zone name
     * 
     * @since Release 1.0.0
     * 
     * @param DateTimeZone $zone
     * 
     * @return string valid EWS time zone name on success, or null on failure
     */ 
	public static function fromDateTimeZone(DateTimeZone $zone): ?string {

		// convert IANA time zone name to EWS
		$zone = self::fromIANA($zone->getName());
		// evaluate if we recieve a converted name
		// return time zone name if zone was found
		if (!empty($zone)) {
			return $zone;
		}
		else {
			return null;
		}

    }

	/**
     * Converts EAS (Microsoft/Windows) time zone name to DateTimeZone object
     * 
     * @since Release 1.0.0
     * 
     * @param string $name  eas time zone name
     * 
     * @return DateTimeZone valid DateTimeZone object on success, or null on failure
     */ 
	public static function toDateTimeZone(string $name): ?DateTimeZone {

		// evaluate if name exists in conversion table
		if (isset(self::$eastoiana[$name])) {
			$zone = @timezone_open(self::$eastoiana[$name]);
		}
		// otherwise attempt to convert time zone name
		else {
			$zone = @timezone_open($name);
		}
		// return time zone object if zone was found
		if ($zone instanceof DateTimeZone) {
			return $zone;
		}
		else {
			return null;
		}

    }
	
